pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IERC4626 is IERC20 {

    event Deposit(address indexed caller, address indexed owner, uint256 assets, uint256 shares);

    event Withdraw(
        address indexed caller,
        address indexed receiver,
        address indexed owner,
        uint256 assets,
        uint256 shares
    );

    function deposit(uint256 assets, address receiver) external returns (uint256 shares);

    // function mint(uint256 shares, address receiver) external returns (uint256 assets);

    function withdraw(
        uint256 assets,
        address receiver,
        address owner
    ) external returns (uint256 shares);

    function redeem(
        uint256 shares,
        address receiver,
        address owner
    ) external returns (uint256 assets);

    function totalAssets() external view returns (uint256);

    function convertToShares(uint256 assets) external view returns (uint256);

    function convertToAssets(uint256 shares) external view returns (uint256);

    function previewDeposit(uint256 assets) external view returns (uint256);

    // function previewMint(uint256 shares) external view returns (uint256);

    function previewWithdraw(uint256 assets) external view returns (uint256);

    // function previewRedeem(uint256 shares) external view returns (uint256);

    function maxDeposit(address) external view returns (uint256) ;

    // function maxMint(address) external view returns (uint256);

    function maxWithdraw(address owner) external view returns (uint256);

    // function maxRedeem(address owner) external view returns (uint256);

}

contracts/test/aave/MockAavePool.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {RESERVE_CONFIG_MAP_UNCAPPED_ACTIVE} from "../../aave/external/Constants.sol";
import {MockAsset} from "../MockAsset.sol";
import {DataTypes} from "../../aave/external/DataTypes.sol";
import {MockAToken} from "./MockAToken.sol";

// NOTE: Yield Simulation Design
// To mock yield accrual, we increase the vault's aToken asset balance.
// But, this will lead to scenarios where there are more aTokens to redeem
// Than there are underlying assets in this mock pool.
// As such, avoid using the deal cheatcode in Foundry and instead
// implement utility functions which retain 1:1 asset:aToken parity

contract MockAavePool {
    uint256 constant SCALE = 1e4;

    MockAToken public aToken;

    MockAsset public underlying;

    uint256 public reserveConfigMap;

    constructor(MockAToken _aToken, MockAsset _underlying) {
        aToken = _aToken;
        underlying = _underlying;
        setReserveConfigMap(RESERVE_CONFIG_MAP_UNCAPPED_ACTIVE);
    }

    // For mock test purposes
    function setReserveConfigMap(uint256 _reserveConfigMap) public {
        reserveConfigMap = _reserveConfigMap;
    }

    function getReserveData(address) public view returns (DataTypes.ReserveData memory) {
        return
            DataTypes.ReserveData({
                //stores the reserve configuration
                configuration: DataTypes.ReserveConfigurationMap({data: reserveConfigMap}),
                //the liquidity index. Expressed in ray
                liquidityIndex: 1e27,
                //the current supply rate. Expressed in ray
                currentLiquidityRate: 0,
                //variable borrow index. Expressed in ray
                variableBorrowIndex: 1e27,
                //the current variable borrow rate. Expressed in ray
                currentVariableBorrowRate: 0,
                //the current stable borrow rate. Expressed in ray
                currentStableBorrowRate: 0,
                //timestamp of last update
                lastUpdateTimestamp: 0,
                //the id of the reserve. Represents the position in the list of the active reserves
                id: 0,
                //aToken address
                aTokenAddress: address(aToken),
                //stableDebtToken address
                stableDebtTokenAddress: address(0),
                //variableDebtToken address
                variableDebtTokenAddress: address(0),
                //address of the interest rate strategy
                interestRateStrategyAddress: address(0),
                //the current treasury balance, scaled
                accruedToTreasury: 0,
                //the outstanding unbacked aTokens minted through the bridging feature
                unbacked: 0,
                //the outstanding debt borrowed against this asset in isolation mode
                isolationModeTotalDebt: 0
            });
    }

    function supply(address _asset, uint256 _amount, address _onBehalfOf, uint16) public {
        IERC20(_asset).transferFrom(msg.sender, address(this), _amount);
        aToken.mint(address(this), _onBehalfOf, _amount, 0);
        IERC20(_asset).transfer(address(aToken), _amount);
    }

    function withdraw(address _asset, uint256 _amount, address _receiver) public returns (uint256) {
        _asset;

        aToken.burn(msg.sender, _receiver, _amount, 0);
        underlying.transfer(_receiver, _amount);
        return _amount;
    }

    // Mints recipient new tokens based on current aToken balance
    // and equivalent value in underlying to this pool
    // to simulate new yield accrued (maintains 1:1 parity)
    // _yield is measured in basis points (1/1e4)
    function simulateYield(address _recipient, uint256 _yield) public {
        uint256 valueAccr = (aToken.balanceOf(_recipient) * _yield) / SCALE;

        underlying.mint(address(this), valueAccr);
        aToken.mint(address(this), _recipient, valueAccr, 0);
    }
}

contracts/test/aave/MockAToken.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockAToken is ERC20 {
    address internal _underlyingAsset;

    constructor(address underlyingAsset) ERC20("Mock aToken", "MAT") {
        _underlyingAsset = underlyingAsset;
    }

    function mint(address caller, address onBehalfOf, uint256 amount, uint256 index) external returns (bool) {
        caller;
        index;
        _mint(onBehalfOf, amount);
        return true;
    }

    function burn(address from, address receiverOfUnderlying, uint256 amount, uint256 index) external {
        index;
        _burn(from, amount);
        if (receiverOfUnderlying != address(this)) {
            ERC20(_underlyingAsset).transfer(receiverOfUnderlying, amount);
        }
    }

    function scaledTotalSupply() public view returns (uint256) {
        return totalSupply();
    }
}

